/**
 * @typedef Tag2
 * @property {string} name
 * @property {any} value
 *
 * @typedef Context
 * @property {string} id - the transaction id to be verified
 * @property {any} input
 * @property {any} wallet
 * @property {Tag2[]} tags
 *
 * @callback VerifyProcess
 * @param {Context} ctx
 * @returns {Async<Context>}
 *
 * @typedef Env6
 * @property {any} loadProcessMeta
 *
 * @param {Env6} env
 * @returns {VerifyProcess}
 */
export function verifyProcessWith(env: Env6): VerifyProcess;
export type Env5 = {
    loadProcessMeta: any;
};
export type VerifyTags = (id: string, env: Env5) => any;
export type Tag2 = {
    name: string;
    value: any;
};
export type Context = {
    /**
     * - the transaction id to be verified
     */
    id: string;
    input: any;
    wallet: any;
    tags: Tag2[];
};
export type VerifyProcess = (ctx: Context) => Async<Context>;
export type Env6 = {
    loadProcessMeta: any;
};
